select t1.user_id,ifnull(t1.content, "") as content,t2.goods_name,DATE_FORMAT(t1.add_time,'%Y-%m-%d') as add_time, ifnull(t1.again_content,"") as again_content,t1.comment_id from zjc_comment t1 left join zjc_goods t2 on t1.goods_id = t2.goods_id
t1.content like concat(concat('%',#{content}),'%')
and t1.user_id = #{user_id}
and t2.store_id = #{store_id}
