mapper.update:
UPDATE zjc_goods


          store_count = store_count - #{Goods_nums, jdbcType=INTEGER},sales_sum = sales_sum + #{Goods_nums, jdbcType=INTEGER}  


WHERE  store_count >= #{Goods_nums, jdbcType=INTEGER} and goods_id = #{goods_id}

mapper.select:
